mapper.update:
update user
set user_password = #{password}
where user_id = #{userId}

mapper.select:
select *
from user
where user_id = #{userId}
  and user_password = #{password};

SELECT u.user_id AS stuId, s.stu_name AS name, s.stu_sex AS sex, CASE WHEN s.stu_sex = 0 THEN '男' WHEN s.stu_sex = 1 THEN '女' END AS sexString, s.stu_grade AS grade, c.category_name AS category, s.stu_class AS stuClass, u.user_password AS password, u.phone AS phone, u.email AS email FROM user u LEFT JOIN student s ON u.user_id = s.stu_id LEFT JOIN category c ON s.category_id = c.category_id
